import React,{Component} from 'react';
import {connect} from 'react-redux';
class list extends Component{

handleitem=()=>{
     var name=document.getElementById("data").value;
 var item={
     id:this.props.items.length+1,
     name:name
 }
     console.log(item);  
this.props.add(item);

}

componentDidMount=()=>{
    this.props.get();
}
    render(){
        var items=this.props.items;
   var names=items.map((item)=>{
        return <div key={item.id} className="d-flex"><li>{item.name}</li>
        <button type="submit" onClick={()=>this.props.remove(item.id)}>del</button></div>
        });
return(
<div className="col-md-6 add">
<input type="text" placeholder="add item" id="data"/>
<button type="submit" onClick={()=>this.handleitem()}>add</button>
<ul>
{names}

</ul>
</div>
);
    }
    }

    function mapStateToProps(state){
            return {items:state.items};
    }
    function mapDispatchToProps(dispatch){
        return{
            get:()=>{
                return fetch('/api/items')
                .then(response=>response.json())
                .then(data=>this.setState({items:data}))
                    dispatch({type:"get"})
            },

            add:(item)=>{
         // const i=pros.push(item);
console.log(item);

             dispatch({type:"add",payload:item})
            },
            remove:(id)=>{

             dispatch({type:"remove",payload:id})
            }
        }
    }
export default connect(mapStateToProps,mapDispatchToProps)(list);
