class list extends Component{
state={
    items:[
        {id:0,name:"honor 7x"},
        {id:1,name:"samsung"},
        {id:2,name:"vivo"},
    ]
}
handleitem=()=>{
    var name=document.getElementById("data").value;
console.log(name);  
  this.setState(state=>({
      items:[...state.items,{id:this.state.items.length+1,name:name}]
  }));

}

del=(id)=>{
   // console.log(id); 
  }

    render(){
    var items=this.state.items.map((item)=>{
        return <div key={item.id} className="d-flex"><li >{item.name}</li>
        <button onClick={this.del(item.id)}>del</button></div>
        });
return(
<div className="col-md-6 add">
<input type="text" placeholder="add item" id="data"/>
<button type="submit" onClick={this.handleitem}>add</button>
<ul>
{items}

</ul>
</div>
);
    }
    }
export default list;
