console.log(state);
switch(action.type){

    case "get":
     return fetch('/api/items')
     .then(response=>response.json())
     .then(data=>this.setState({items:data}))
     ;

    case "add":
    return {...state,items:[action.payload,...state.items]};
    case "remove":
    return {...state,
        items:state.items.filter(item=>item.id!==action.payload)    
    };
    default:
return state;
