fetch('/api/items')
.then(response=>response.json())
.the

var initialState={

    items:[
        {id:0,name:"honor 7x"},
        {id:1,name:"samsung"},
        {id:2,name:"vivo"},
    ]

}
const rootReducer=(state=initialState,action)=>{
console.log()
switch(action.type){

    case "get":
    return {...state};
    case "add":
    return {...state,items:[action.payload,...state.items]};
    case "remove":
    return {...state,
        items:state.items.filter(item=>item.id!==action.payload)    
    };
    default:
return state;
}

}
export default rootReducer;
